import "../style/contact.css"
import emailjs from '@emailjs/browser';

function Contact() {
  const form =useRef()
  const calling =(e)=>{
    e.preventDefault();
    if("name"|| "email" ==""){
      alert("Fill the detail then sand the ")
    }else{
    emailjs.sendForm('service_nlcsa2s', 'template_0s90k5w', form.current, 'Xrm1ORmUAXnT4AqKS')
    .then((result) => {
        console.log(result.text);
    }, (error) => {
        console.log(error.text);
    });}
    e.target.reset()

}
  return <div className="formcantinor">
    <form className="form" ref={form} onSubmit={calling}>
      <label>Name</label>
      <input type="text" placeholder="Enter your name" value="name" name="to_name"/>
      <label>E-mail </label>
      <input type="e-mail" placeholder="Enter e-mail" value ="email" name="from_name"/>
      <label> contact Number </label>
      <input type="number" placeholder="Enter subject" name="to-contact" />
      <label>Massage </label>
      <input className="massage" type="text" placeholder="text here!" name="message" />
   <button className="sumbitbtn" type="sumbit">Sumbit</button>

    </form>
  </div>;
}

export default Contact;
